package pt.ulisboa.tecnico.meic.cnv.mazerunner.maze.render;

import pt.ulisboa.tecnico.meic.cnv.mazerunner.maze.Maze;
import pt.ulisboa.tecnico.meic.cnv.mazerunner.maze.exceptions.InvalidCoordinatesException;

public class RenderMazeHTML implements RenderMaze {

	/**
	 * Returns a string form of the current state of the maze.
	 * HTML (SVG)
	 */
	@Override
	public String render(Maze maze, int velocity) {
		final StringBuffer b = new StringBuffer();
		int radius = 8;
		int radiusSmall = 3;
		int svgWidth = maze.getWidth() * 20 + (2 * 20);
		int svgHeight = maze.getHeight() * 20 + (2 * 20);
		int xSVG = 10;
		int ySVG = 10;
		b.append("<!DOCTYPE html> <html> <body>");
		b.append("<svg width=\"" + svgWidth + "\" height=\"" + svgHeight + "\">");
		
		// Defs
		b.append("<defs>");
		
		b.append(createBedrock(20));
		
		b.append(createWall(20));
		
		b.append("</defs>");
		
		for (int x = 0; x < maze.getWidth() + 2; x++) {
	        b.append(wallBedrock(xSVG, ySVG, 20));	//BEDROCK
	        xSVG += 20;
	    }
		
		xSVG = 10;
		ySVG += 15;
		
		for (int y = 0; y < maze.getHeight(); y++){
	        b.append(wallBedrock(xSVG, ySVG, 20));
	        xSVG += 20;
	        for ( int x = 0; x < maze.getWidth(); x++ ) {
	        	char pos = 'E';
				try {
					pos = maze.getPos(x, y);
				} catch (InvalidCoordinatesException e) {
					e.printStackTrace();
				}
	        	if(pos == Maze.WALL_CHAR) {
	        		b.append(wallRegular(xSVG, ySVG,20));
	        	} else if(pos == Maze.VISITED_CHAR) {
	        		b.append(circles(xSVG, ySVG, radiusSmall, "grey"));
	        	} else if(pos == Maze.INITIAL_CHAR) {
	        		b.append(circles(xSVG, ySVG, radius, "green"));	
	        	} else if(pos == Maze.FINAL_CHAR) {
	        		b.append(circles(xSVG, ySVG, radius, "orange"));	
	        	}
	        	xSVG += 20;
	        }
	        b.append(wallBedrock(xSVG, ySVG, 20));
	        xSVG = 10;
	        ySVG += 15;
	    }
		
		for (int x = 0; x < maze.getWidth() + 2; x++) {
			 b.append(wallBedrock(xSVG, ySVG, 20));
			 xSVG += 20;
	    }
		
		b.append("</svg>");
		b.append("</body> </html>");
		return b.toString();
	}
	
	private String createBedrock(int size) {
		return String.format("<g id=\"Bedrock\"> <image width=\"%d\" height=\"%d\" xlink:href=\"data:image/jpeg;base64,\" /> </g>", size, size);
	}
	
	private String createWall(int size) {
		return String.format("<g id=\"Wall\"> <image width=\"%d\" height=\"%d\" xlink:href=\"data:image/jpeg;base64,\" /> </g>", size, size);
	}
	
	private String circles(int x, int y, int radius, String color) {
		return String.format("<circle cx=\"%d\" cy=\"%d\" r=\"%d\" fill=\"%s\" />", x, y, radius, color);
	}
	
	private String wallRegular(int x, int y, int size) {
		return String.format("<use xlink:href=\"#Wall\" x=\"%d\" y=\"%d\" />",
				x - (size/2), y - (size/2), size, size);
	}
	
	private String wallBedrock(int x, int y, int size) {
		return String.format("<use xlink:href=\"#Bedrock\" x=\"%d\" y=\"%d\" />",
				x - (size/2), y - (size/2), size, size);
	} 

}
